function lazyLoad(){const e=new IntersectionObserver((e,t)=>{e.forEach(e=>{if(e.isIntersecting){const n=e.target;let s=n.style.getPropertyValue("--lazybg_");n.style.removeProperty("--lazybg_");let i=s.substring(5,s.length-2),o=document.createElement("img");o.src=i,o.addEventListener("load",e=>{o=null,n.style.setProperty("--lazycol","none"),n.style.setProperty("--lazybg",s),n.classList.remove("lazyload"),t.unobserve(n)})}})}),t=document.querySelectorAll(".lazyload");t.forEach(t=>{let n=t.style.getPropertyValue("--lazybg");t.style.setProperty("--lazybg","none"),t.style.setProperty("--lazybg_",n),e.observe(t)})}lazyLoad()